\usetikzlibrary{arrows.meta}
\begin{tikzpicture}
  \draw [help lines] (0,0) grid (5,4);
  % draw axes
  \draw[-&gt;] (0,0) -- (5.2,0) node[right]{$x$};
  \draw[-&gt;] (0,0) -- (0,4.2) node[above]{$y$};

  % draw rectangle
  \draw [very thick,red] (1,1) rectangle (3,2);

  %draw point
  \node[fill, circle, blue] at (5,4) {};

  % draw arrow line
  \draw [-Stealth,thick, shorten &gt;=2mm] (3,2) -- (5,4);
\end{tikzpicture}

These drawings are best done as nodes, edges and shapes.
\begin{tikzpicture} %-- style definitions
  [nd/.style={draw, thick, minimum width=2cm, minimum height=1cm},
  lbl/.style={yshift=2mm},
  e/.style={red,fill=white}]

  % node 1
  \node[nd, fill=yellow!40] (webQ) at (1,5.5){WebQ};
  \node[lbl] at (webQ.north west) {qmd};

  % node 2
  \node[nd, fill=yellow!40] (qSite) at (1,1.5){qSite};
  \node[lbl] at (qSite.north west) {html};
  \draw (webQ) edge[-&gt;] node[e]{render}(qSite);

   % node 3
  \node[nd, fill=cyan!20] (ghDesk) at (6,1.5){ghDesk};
  \node[lbl] at (ghDesk.north west) {html};
  \draw (qSite) edge[-&gt;] node[e]{rsync}(ghDesk);

  % node 4
  \node[nd, fill=orange!20] (github) at (9,5.5){github};
  \node[lbl] at (github.north west) {html};
  \draw (ghDesk) edge[-&gt;] node[e]{commit}(github);   
\end{tikzpicture}


